var app = angular.module('shocktrade');

app.factory("AdminService", function($http, $q) {
	var service = {};
	
	service.createBugReport = function(report) {
		return $http({
			method : 'PUT',
			url : '/api/admin/bugreport/',
			data: angular.toJson(report)
		});
	}
	
	service.getBugReport = function(id) {
		return $http.get('/api/admin/bugreport/' + id);
	}
	
	service.getBugReports = function() {
		return $http.get('/api/admin/bugreport/list');
	}
	
	service.removeBugReport = function(id) {
		return $http.delete('/api/admin/bugreport/' + id);
	}
	
	service.getSessions = function() {
		return $http.get('/api/admin/session/list');
	}
	
	service.invokeProcess = function(id, symbol) {
		return $http.get('/api/admin/process/' + id + '/invoke/' + symbol);
	}
	
	return service;
});