/**
 * Invite Player Dialog Service and Controller
 * @author lawrence.daniels@gmail.com
 */
(function () {
    var app = angular.module('shocktrade');

    /**
     * Invite Player Dialog Singleton
     * @author lawrence.daniels@gmail.com
     */
    app.factory('InvitePlayerDialog', function ($http, $log, $modal, $q) {
        var service = {};

        /**
         * Invite a player via pop-up dialog
         */
        service.invitePlayer = function () {

            var modalInstance = $modal.open({
                templateUrl: 'invite_player_dialog.htm',
                controller: 'PlayerInviteCtrl',
                resolve: {
                    invites: function () {
                        return $scope.invites;
                    }
                }
            });

            modalInstance.result.then(function (invites) {
                $log.info("invites = " + angular.toJson(invites));
                // $scope.invites = invites;
            }, function () {
                $log.info('Modal dismissed at: ' + new Date());
            });
        };

        return service;
    });

    /**
     * Player Invite Controller
     * @author lawrence.daniels@gmail.com
     */
    app.controller('PlayerInviteCtrl', ['$scope', '$modalInstance', function ($scope, $modalInstance) {

        $scope.invites = [];

        $scope.getInvitedCount = function () {
            var count = 0;
            var invites = $scope.invites;
            for (var n = 0; n < invites.length; n++) {
                if (invites[n]) count += 1;
            }
            return count;
        };

        $scope.ok = function () {
            $modalInstance.close($scope.invites);
        };

        $scope.cancel = function () {
            $modalInstance.dismiss('cancel');
        };

    }]);

})();
