/**
 * Player Invite Controller
 * @author lawrence.daniels@gmail.com
 */
angular
	.module('shocktrade')
	.controller('PlayerInviteCtrl', ['$scope', '$modalInstance', function($scope, $modalInstance) {
	
	$scope.invites = [];

	$scope.getInvitedCount = function() {
		var count = 0;
		var invites = $scope.invites;
		for(var n = 0; n < invites.length; n++) {
			if(invites[n]) count += 1;
		}
		return count;
	};
	
	$scope.ok = function () {
		$modalInstance.close($scope.invites);
	};

	$scope.cancel = function () {
		$modalInstance.dismiss('cancel');
	};
	
}]);

